import React from 'react';
import {getMergeSortAnimations} from '../sortingAlgorithms/sortingAlgorithms.js';
import {getQuickSortAnimations} from '../sortingAlgorithms/quickSort.js';
import { getHeapSortAnimations } from "../sortingAlgorithms/heapSort.js";
import { getBubbleSortAnimations } from "../sortingAlgorithms/bubbleSort.js";
import { Button } from "../Button"




import './SortingVisualizer.css';
import { MenuItems } from "./MenuItems"
import styled, { css } from 'styled-components';

 var ANIMATION_SPEED_MS = 3;

 var NUMBER_OF_ARRAY_BARS = 220;


const PRIMARY_COLOR = "rgb(0, 255, 191)";

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = "red";

export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            array: [],
        };
    }

    speedAndBars = x => {
      if (x === 1) {
        this.genRandomArray(153, 1);
      } else if (x === 2) {
        this.genRandomArray(200, 2);
      } else if (x === 3) {
        this.genRandomArray(275, 3);
      } else if (x === -1) {
        this.genRandomArray(100, -1);
      } else if (x === -2) {
        this.genRandomArray(55, -2);
      }
    };

   /* resetColor = () => {
      const arrayBars = document.getElementsByClassName("array-bar");
      for (let i = 0; i < arrayBars.length; i++) {
        arrayBars[i].style.backgroundColor = PRIMARY_COLOR;
      }
    };*/

    genRandomArray = (numberOfArrayBars, number) => {
      // console.log("height ", window.innerHeight);
      // console.log("width ", window.innerWidth);
      // if (window.innerWidth >= 1400) {
      //   numberOfArrayBars -= 20;
      // }
      NUMBER_OF_ARRAY_BARS = numberOfArrayBars;

      if (number === 1) {
        let array = [];
        for (let i = 0; i < numberOfArrayBars; i++) {
          array.push(randomIntFromInterval(10, 645));
        }
        document.querySelector(".x").style.backgroundColor = "red";
       // document.querySelector(".xByFour").style.backgroundColor = "grey";
       // document.querySelector(".xByTwo").style.backgroundColor = "grey";
       // document.querySelector(".xMultiplyTwo").style.backgroundColor = "grey";
       // document.querySelector(".xMultiplyFour").style.backgroundColor = "grey";
        ANIMATION_SPEED_MS = 5;
        this.setState({ array: array });
        //if we have called arrayBars with className 'array-bar', we wouldn't get the current clicked speed/bar button but the previous selection as setState is an asynchrnous so it doesn't update instantly, so we created seperate function for handling async function and get current state of the arrayBars
        this.updateBarsState(6);
      } else if (number === 2) {
        let array = [];
        for (let i = 0; i < numberOfArrayBars; i++) {
          array.push(randomIntFromInterval(10, 645));
        }
      //  document.querySelector(".x").style.backgroundColor = "grey";
       // document.querySelector(".xByFour").style.backgroundColor = "grey";
        //document.querySelector(".xByTwo").style.backgroundColor = "grey";
        document.querySelector(".xMultiplyTwo").style.backgroundColor = "red";
        //document.querySelector(".xMultiplyFour").style.backgroundColor = "grey";
        ANIMATION_SPEED_MS = 4;
        this.setState({ array: array });
        this.updateBarsState(4.3);
      } else if (number === 3) {
        let array = [];
        for (let i = 0; i < numberOfArrayBars; i++) {
          array.push(randomIntFromInterval(10, 645));
        }
        //document.querySelector(".x").style.backgroundColor = "grey";
        //document.querySelector(".xByFour").style.backgroundColor = "grey";
        //document.querySelector(".xByTwo").style.backgroundColor = "grey";
        //document.querySelector(".xMultiplyTwo").style.backgroundColor = "grey";
        document.querySelector(".xMultiplyFour").style.backgroundColor = "red";
        ANIMATION_SPEED_MS = 2;
        this.setState({ array: array });
        this.updateBarsState(2.75);
      } else if (number === -1) {
        let array = [];
        for (let i = 0; i < numberOfArrayBars; i++) {
          array.push(randomIntFromInterval(10, 645));
        }

       // document.querySelector(".x").style.backgroundColor = "grey";
       // document.querySelector(".xByFour").style.backgroundColor = "grey";
        document.querySelector(".xByTwo").style.backgroundColor = "red";
       // document.querySelector(".xMultiplyTwo").style.backgroundColor = "grey";
       // document.querySelector(".xMultiplyFour").style.backgroundColor = "grey";
        ANIMATION_SPEED_MS = 8;
        this.setState({ array: array });
        this.updateBarsState(9.5);
      } else if (number === -2) {
        let array = [];
        for (let i = 0; i < numberOfArrayBars; i++) {
          array.push(randomIntFromInterval(10, 645));
        }
      //  document.querySelector(".x").style.backgroundColor = "grey";
        document.querySelector(".xByFour").style.backgroundColor = "red";
      //  document.querySelector(".xByTwo").style.backgroundColor = "grey";
       // document.querySelector(".xMultiplyTwo").style.backgroundColor = "grey";
       // document.querySelector(".xMultiplyFour").style.backgroundColor = "grey";
        ANIMATION_SPEED_MS = 50;
        this.setState({ array: array });
        this.updateBarsState(16);
      }
      //this.resetColor();
    };

    updateBarsState = width => {
      // this.setState({ array: array });
      let arrayBars = [];
      async function something() {
        const a = new Promise((res, rej) => {
          const arrayBars = document.getElementsByClassName("array-bar");
          res(arrayBars);
        });
        const newArrayBars = await a;
        arrayBars = newArrayBars;
        for (let k = 0; k < arrayBars.length; k++) {
          arrayBars[k].style.width = `${width}px`;
        }
        // console.log(newArrayBars.length);
        // console.log(arrayBars.length);
      }
      something();
    };


    componentDidMount() {
        this.resetArray();
    }

    resetArray(){
        const array = [];
        for(let i = 0; i < NUMBER_OF_ARRAY_BARS; i++) {
            array.push(randomIntFromInterval(5,705));
        }
        this.setState({array});
    }

    mergeSort() {
        const animations = getMergeSortAnimations(this.state.array);
        for (let i = 0; i < animations.length; i++) {
          const arrayBars = document.getElementsByClassName('array-bar');
          const isColorChange = i % 3 !== 2;
          if (isColorChange) {
            const [barOneIdx, barTwoIdx] = animations[i];
            const barOneStyle = arrayBars[barOneIdx].style;
            const barTwoStyle = arrayBars[barTwoIdx].style;
            const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            setTimeout(() => {
              barOneStyle.backgroundColor = color;
              barTwoStyle.backgroundColor = color;
            }, i * ANIMATION_SPEED_MS);
          } else {
            setTimeout(() => {
              const [barOneIdx, newHeight] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              barOneStyle.height = `${newHeight}px`;
            }, i * ANIMATION_SPEED_MS);
          }
        }
      }
    quickSort() {
        const animations = getQuickSortAnimations(this.state.array);
        for (let i = 0; i < animations.length; i++) {
          const arrayBars = document.getElementsByClassName("array-bar");
          const isColorChange = i % 4 <= 1;
          //const isColorChange = i % 3 !== 2;
          if (isColorChange) {
            const [barOneIdx, barTwoIdx] = animations[i];
            const barOneStyle = arrayBars[barOneIdx].style;
            const barTwoStyle = arrayBars[barTwoIdx].style;
            const color = i % 4 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            //const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            setTimeout(() => {
              barOneStyle.backgroundColor = color;
              barTwoStyle.backgroundColor = color;
            }, i * ANIMATION_SPEED_MS * 2 );
          } else {
            setTimeout(() => {
              const [barOneIdx, newHeight] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              barOneStyle.height = `${newHeight}px`;
            }, i * ANIMATION_SPEED_MS * 2);
          }
        }
    }

    heapSort() {
      const animations = getHeapSortAnimations(this.state.array);
        for (let i = 0; i < animations.length; i++) {
          const colorChange = i % 4 <= 1;
          const arrayBars = document.getElementsByClassName("array-bar");
          if (colorChange) {
            const [barOneIdx, barTwoIdx] = animations[i];
            const barOneStyle = arrayBars[barOneIdx].style;
            const barTwoStyle = arrayBars[barTwoIdx].style;
            const color = i % 4 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            setTimeout(() => {
              barOneStyle.backgroundColor = color;
              barTwoStyle.backgroundColor = color;
            }, i * ANIMATION_SPEED_MS);
          } else {
            setTimeout(() => {
              const [barOneIdx, newHeight] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              barOneStyle.height = `${newHeight}px`;
            }, i * ANIMATION_SPEED_MS);
          }
        }
    }

    bubbleSort() {
      const animations = getBubbleSortAnimations(this.state.array);
      for (let i = 0; i < animations.length; i++) {
        const colorChange = i % 4 <= 1;
        const arrayBars = document.getElementsByClassName("array-bar");
        if (colorChange) {
          const [barOneIdx, barTwoIdx] = animations[i];
          const barOneStyle = arrayBars[barOneIdx].style;
          const barTwoStyle = arrayBars[barTwoIdx].style;
          const color = i % 4 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
          setTimeout(() => {
            barOneStyle.backgroundColor = color;
            barTwoStyle.backgroundColor = color;
          }, i * ANIMATION_SPEED_MS);
        } else {
          setTimeout(() => {
            const [barOneIdx, newHeight] = animations[i];
            const barOneStyle = arrayBars[barOneIdx].style;
            barOneStyle.height = `${newHeight}px`;
          }, i * ANIMATION_SPEED_MS);
        }
      }

    }

    render(){
        const {array} = this.state;
        return (
            <>
             <nav className="NavbarItems">
                <h1 className="navbar-logo">SortingVisualizer<i class="fab fa-react"></i></h1>
                <div className="menu-icon" onClick={this.handleClick}>
                    <i className={this.state.clicked ? 'fas fa-times' : 'fas fa-bars'}></i>
                </div>
                <div class="btn-toolbar">
                <Button onClick={() => this.resetArray()} style={{float: 'right'}}> New Array</Button>
                <Button onClick={() => this.mergeSort()} style={{float: 'right'}}> Merge Sort </Button>
                <Button onClick={() => this.quickSort()} style={{float: 'right'}}> Quick Sort </Button>
                <Button onClick={() => this.heapSort()} style={{float: 'right'}}> Heap Sort </Button>
                <Button onClick={() => this.bubbleSort()} style={{float: 'right'}}> Bubble Sort </Button>
                </div>
                <div
                  className="btn-toolbar"
                  role="toolbar"
                  aria-label="Toolbar with button groups"
                >
            <div
              className="btn-group mr-2"
              role="group"
              aria-label="First group"
            >
              <Button
                type="button"
                className="btn btn-secondary"
                // disabled
                title="Increase speed and decrease number of array-bars or vice-versa"
              >
                Speed
              </Button>
              <button
                type="button"
                className="btn btn-secondary xByFour"
                title="Decrease in speed and decrease in number of array bars"
                onClick={() => this.speedAndBars(-2)}
              >
                n/4
              </button>
              <button
                type="button"
                className="btn btn-secondary xByTwo"
                title="Decrease in speed by half and decrease in number of bars"
                onClick={() => this.speedAndBars(-1)}
              >
                n/2
              </button>
              <button
                type="button"
                className="btn btn-secondary x"
                title="Default speed and number of array bars"
                onClick={() => this.speedAndBars(1)}
              >
                n
              </button>
              <button
                type="button"
                className="btn btn-secondary xMultiplyTwo"
                title="Increase speed 2x and increase in number of array-bars "
                onClick={() => this.speedAndBars(2)}
              >
                2n
              </button>
              <button
                type="button"
                className="btn btn-secondary xMultiplyFour"
                title="Increase speed 4x and increase in number of array bars"
                onClick={() => {
                  this.speedAndBars(3);
                }}
              >
                4n
              </button>
            </div>
          </div>
          <div>

          </div>

                <ul className={this.state.clicked ? 'nav-menu active' : 'nav-menu'}>
                    {MenuItems.map((item, index) => {
                        return (
                            <li key={index}>
                                <a className={item.cName} href={item.url}>
                                {item.title}
                                </a>
                            </li>
                        )
                    })}
                </ul>


            </nav>

              <div className="array-container">
                {array.map((value, idx) =>  (
                  <div 
                  className="array-bar" 
                  key={idx}
                  style={{height: `${value}px`}}></div>
                ))}
              </div>
            </>
        );
    }
}

function randomIntFromInterval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}

